-- Custom fold text function to show header content nicely
function _G.custom_markdown_foldtext()
  local line = vim.fn.getline(vim.v.foldstart)
  local folded_lines = vim.v.foldend - vim.v.foldstart + 1

  -- Clean up the line (remove markdown syntax)
  local clean_line = line:gsub("^%s*#+%s*", ""):gsub("%s*$", "")

  return string.format("  %s (%d lines)", clean_line, folded_lines)
end

